import { useState } from 'react'

function Square({value, buttonClick}) {

  return <button className="square" onClick={buttonClick}>{value}</button>
}

export default function Board() {
  const [isX, setIsX] = useState(true);
  const [spaces, setSpaces] = useState(Array(9));

  function buttonClick({ myID }) {
    spaces[myID] = 'X';
  }

  return (
    <>
      <div className="board-row">
        <Square value = {spaces[0]} buttonClick = {(id) => buttonClick}/>
        <Square value = {spaces[1]} buttonClick = {buttonClick}/>
        <Square value = {spaces[2]} buttonClick = {buttonClick}/>
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
