import { useState } from 'react'

function Square({ value, buttonClick, iden }) {

  return (<button className="square" onClick={() => buttonClick(iden)}>{value}</button>);
}

function isComplete(spaces) {
  for (let row = 0; row < 3; row++) {
    let myChar = spaces[row * 3];
    if (myChar != ' ' && spaces[row * 3 + 1] == myChar && spaces[row * 3 + 2] == myChar) {
      return true;
    }
  }

  for (let col = 0; col < 3; col++) {
    let myChar = spaces[col];
    if (myChar != ' ' && spaces[col + 3] == myChar && spaces[col + 6] == myChar) {
      return true;
    }
  }
  let myChar = spaces[4];
  return (myChar != ' ') && (spaces[0] == spaces[8] || spaces[2] == spaces[6]);
}

export default function Board() {
  const [isX, setIsX] = useState(true);
  const [spaces, setSpaces] = useState(Array(9).fill(null));
  const [complete, setComplete] = useState("");

  function buttonClick(myID) {
    const spaceCopy = spaces.slice();
    if (isX)
      spaceCopy[myID] = 'X';
    else 
      spaceCopy[myID] = 'O';
    setIsX(!isX);
    setSpaces(spaceCopy);
    if (isComplete(spaces))
      setComplete("d");
    else 
      setComplete("nd");
  }

  return (
    <>
      <div className="board-row">
        <Square iden={0} value={spaces[0]} buttonClick={buttonClick} />
        <Square iden={1} value={spaces[1]} buttonClick={buttonClick} />
        <Square iden={2} value={spaces[2]} buttonClick={buttonClick} />
      </div>
      <div className="board-row">
        <Square iden={3} value={spaces[3]} buttonClick={buttonClick} />
        <Square iden={4} value={spaces[4]} buttonClick={buttonClick} />
        <Square iden={5} value={spaces[5]} buttonClick={buttonClick} />
      </div>
      <div className="board-row">
        <Square iden={6} value={spaces[6]} buttonClick={buttonClick} />
        <Square iden={7} value={spaces[7]} buttonClick={buttonClick} />
        <Square iden={8} value={spaces[8]} buttonClick={buttonClick} />
      </div>
      <p>{complete}</p>

    </>
  )
}
