import { useState } from 'react'

function Square({ value, buttonClick, iden}) {

  return (<button className="square" onClick={(iden) => buttonClick(iden)}>{value}</button>);
}

export default function Board() {
  const [isX, setIsX] = useState(true);
  const [spaces, setSpaces] = useState(Array(9).fill(null));

  function buttonClick(myID) {
    const spaceCopy = spaces.slice();
    spaceCopy[myID] = 'X';
    setSpaces(spaceCopy);
    console.log("Howdy" + spaceCopy);
  }

  return (
    <>
      <div className="board-row">
        <Square iden={0} value={spaces[0]} buttonClick={(mI) => buttonClick(0)} />
        <Square value={spaces[1]} buttonClick={(mI) => buttonClick(1)} />
        <Square value={spaces[2]} buttonClick={(mI) => buttonClick(2)} />
      </div>
      <div className="board-row">
        <Square value={spaces[3]} buttonClick={() => buttonClick(3)} />
        <Square value={spaces[4]} buttonClick={() => buttonClick(4)} />
        <Square value={spaces[5]} buttonClick={() => buttonClick(5)} />
      </div>
      <div className="board-row">
        <Square value={spaces[6]} buttonClick={() => buttonClick(6)} />
        <Square value={spaces[7]} buttonClick={() => buttonClick(7)} />
        <Square value={spaces[8]} buttonClick={() => buttonClick(8)} />
      </div>

    </>
  )
}
