import { useState } from 'react'

function Square({value, buttonClick}) {

  return (<button className="square" onClick={buttonClick}>{value}</button>)
}

export default function Board() {
  const [isX, setIsX] = useState(true);
  const [spaces, setSpaces] = useState(Array(9));

  function buttonClick({ myID }) {
    let spaceCopy = spaces.slice();
    spaceCopy[myID] = 'X';
    setSpaces(spaceCopy);
    console.log("Howdy" + myID);
  }

  return (
    <>
      <div className="board-row">
        <Square value = {spaces[0]} buttonClick = {() => buttonClick(0)}/>
        <Square value = {spaces[1]} buttonClick = {() => buttonClick(1)}/>
        <Square value = {spaces[2]} buttonClick = {() => buttonClick(2)}/>
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>

    </>
  )
}
